// });

const InputMask = React.forwardRef(({ onChange, ...rest }, ref) => (
  <MaskedInput
    {...rest}
    ref={ref}
    className="rs-input"
    onChange={(event) => {
      onChange(event.target.value);
    }}
  />
));

const TextField = React.forwardRef((props, ref) => {
  const { name, label, accepter, ...rest } = props;
  return (
    <Form.Group controlId={`${name}-4`} ref={ref}>
      <Form.ControlLabel>{label} </Form.ControlLabel>
      <Form.Control name={name} accepter={accepter} {...rest} />
    </Form.Group>
  );
});

const mask = [
  "(",
  /[1-9]/,
  /\d/,
  ")",
  " ",
  /\d/,
  /\d/,
  /\d/,
  "-",
  /\d/,
  /\d/,
  /\d/,
  /\d/,
  /\d/,
  /\d/,
  /\d/,
];

const Signup = () => {
  const formRef = React.useRef();
  // const [formError, setFormError] = React.useState({});
  // const [formValue, setFormValue] = React.useState({
  //   name: "",
  //   email: "",
  //   phone: "",
  //   password: "",
  //   verifyPassword: "",
  // });

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [phone_number, setPhone_number] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");
  const [redirect, setRedirect] = useState(false);
  const [isError, setIsError] = useState('');


  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://127.0.0.1:8000/api/register", {
        "username": username,
        "email": email,
        "phone_number": phone_number,
        "password": password,
        "password2": passwordConfirm,
      })
      .then((res) => {
        console.log(res);
        setRedirect(true);
      })
      .catch((error) => {
        console.log("catch");
        console.log(error.response.data);
        // console.log(error.response.data);
        // let err = 'please check the entered fields correctly...'
        // if(error.response.data.email) err = error.response.data.email
        // else if(error.response.data.password) err = error.response.data.password
        // else if(error.response.data.username) err = error.response.data.username
        // alert.error(err)
      });
  };
  if (redirect) {
    return <Redirect to="/" />;
  }

  const checkValidation=(e)=>{
    const confpass = e.target.value;
    setPasswordConfirm(confpass);
    if(password != confpass){
      setIsError("Password do not Matched");
    } else{
      setIsError("");
    }
   };

  return (
    <Container>
      <Row className="justify-content-center">
        <Col xs={12} md={6} xl={6} className="login">
          <Card className="shadow e" style={{ width: "30rem" }}>
            <FlexboxGrid>
              <FlexboxGrid.Item>
                <div className="align-items-center d">
                  <h2 className="mb-0">Sign up</h2>
                </div>
                <Form
                  ref={formRef}
                  onSubmit={(e) => handleSubmit(e)}
                  // onChange={setFormValue}
                  // onCheck={setFormError}
                  // formValue={formValue}
                  // model={model}
                >
                  <TextField
                    name="username"
                    label="Username"
                    className="fields"
                    onChange={(e) => setUsername(e.target.value)}
                  />
                  <TextField
                    name="email"
                    label="Email"
                    className="fields"
                    onChange={(e) => setEmail(e.target.value)}
                  />
                  <TextField
                    name="phone_number"
                    label="Phone Number"
                    mask={mask}
                    accepter={InputMask}
                    className="fields"
                    onChange={e=>setPhone_number(e.target.value)}
                  />
                  <TextField
                    name="password"
                    label="Password"
                    type="password"
                    autoComplete="off"
                    className="fields"
                    onChange={e=>setPassword(e.target.value)}
                  />
                  <TextField
                    name="passwordConfirm"
                    label="Confirm password"
                    type="password"
                    autoComplete="off"
                    className="fields"
                    onChange={(e) => checkValidation(e)}
                  />

                  <ButtonToolbar className="wrapper">
                    <Button
                      appearance="primary"
                      className="justify-content-center item1"
                      type="submit"
                      onSubmit={handleSubmit}
                    >
                      Sign up
                    </Button>
                  </ButtonToolbar>

                  <div className="d-flex justify-content-center align-items-center mt-4 l">
                    <span className="fw-normal">
                      Already have Account?{" "}
                      <Link to="/signin" className="fw-bold aa">
                        Sign In
                      </Link>
                    </span>
                  </div>
                </Form>
              </FlexboxGrid.Item>
            </FlexboxGrid>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};
export default Signup;

