import React, { useEffect } from "react";
import img from "../images/pricing.jpg";
import Back from "../common/Back";
import "./buy.css";
import { useLocation } from "react-router-dom";
import QueryString from "query-string";
import { API_URL } from "../config/index";

const Buy = () => {
  const location = useLocation();

  useEffect(() => {
    // Check to see if this is a redirect back from Checkout
    // const query = new URLSearchParams(window.location.search);
    const values = QueryString.parse(location.search);

    if (values.success) {
      console.log("Order placed! You will receive an email confirmation.");
    }

    if (values.canceled) {
      console.log(
        "Order canceled -- continue to shop around and checkout when you're ready."
      );
    }
  }, []);
  return (
    <>
      <section className="contact mb">
        <Back name="Buy" title="Buy Properties" cover={img} />
        <div className="container">
          <form className="shadow">
            <div className="product">
              <img
                src="https://i.imgur.com/EHyR2nP.png"
                alt="The cover of Stubborn Attachments"
              />
              <div className="description">
                <h3>Stubborn Attachments</h3>
                <h5>$20.00</h5>
              </div>
            </div>
            <form
              action={`${API_URL}/api/stripe/create-checkout-session`}
              method="POST"
            >
              <button className="button" type="submit">
                Checkout
              </button>
            </form>

            <div className="box shadow" key={index}>
              <div className="img">
                <img
                  src={`http://127.0.0.1:8000${cdata.images}`}
                  alt="property"
                />
              </div>
              <div className="text">
                <div className="category flex">
                  <span className="rec">{cdata.category}</span>
                  {/* <i className='fa fa-heart'></i> */}
                </div>
                <h4>{cdata.name}</h4>
                <p>
                  <i className="fa fa-location-dot"></i> {cdata.address}
                </p>
                <p>{cdata.price} PKR</p>
              </div>
              <hr />
              {/* <div className="button flex">
                <div>
                  <button className="btn2">Buy Now</button>
                </div>
              </div> */}
            </div>
          </form>
        </div>
      </section>
    </>
  );
};

export default Buy;

