import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faAngleLeft,
  faEnvelope,
  faUnlockAlt,
} from "@fortawesome/free-solid-svg-icons";
import {
  Col,
  Row,
  Form,
  Card,
  Button,
  Container,
  InputGroup,
} from "react-bootstrap";

import { Link, useHistory } from "react-router-dom";
import { Routes } from "../routes";
import jwt_decode from "jwt-decode"; // import dependency
import {useAlert} from 'react-alert'
const Signin = () => {
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");
  const alert = useAlert()
  const history = useHistory();
  const [error, setError] = useState("");
  const patientdashboard = (data) => {
    if (data.user.type == "Patient") {
      if (data.is_new_registration) {
        console.log(data);
        history.push("/signin/newpatientregistration");
      } else {
        history.push("/all_clinics");
      }
    } else if (data.user.type == "Manager") {
      history.push("/doctor/doctordashboard");
    } else if (data.user.type == "Admin") {
      history.push("/manager/addclinic");
    }
  };
  let loginStatusCode;
  const handleSubmit = async (e) => {
    e.preventDefault();

    fetch("https://server.nichecares.com/auth/login/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    })
      .then((res) => {
        loginStatusCode = res.status;
        return res.json();
      })
      .then((response) => {
        console.log(response);

        if (loginStatusCode >= 400 && loginStatusCode <= 499) {
          if (response.detail) {
            setError(response.detail);
            alert.error('invalid Email or Password...')

          } else {
            setError("invalid Credentials");

          }
        } else {
          localStorage.setItem("Authorization", `${response.access_token}`);
          // document.getElementById("log_cre").innerHTML = "";
          patientdashboard(jwt_decode(response.access_token));
          setError("");
          alert.success('Sign In Successfully...')

        }
      })
      .catch((error) => {
        // document.getElementById("log_cre").innerHTML =error.response.data.detail;
        console.log(error);
        setError("Network error");
        alert.error('Error in Network or server')

      });
  };

  return (
    <>
      <section className="d-flex align-items-center my-5 mt-lg-6 mb-lg-5">
        <Container>
          <p className="text-center">
            <Card.Link
              className="text-muted"
              as={Link}
              to={Routes.Landing.path}
            >
              <FontAwesomeIcon icon={faAngleLeft} className="me-2" /> Back to
              homepage
            </Card.Link>
          </p>
          <Row className="justify-content-center">
            <Col
              xs={12}
              md={6}
              xl={6}
              className="d-flex align-items-center justify-content-center"
            >
              <div className="bg-light shadow shadow-soft border rounded-pill border-light p-4 p-lg-5 w-100 fmxw-500">
                <div className="text-center text-md-center mb-4 mt-md-0">
                  <h3 className="mb-0">Sign in</h3>
                </div>
                <Form className="mt-4">
                  <Form.Group id="email" className="mb-4">
                    <Form.Label>Your Email</Form.Label>
                    <InputGroup>
                      <InputGroup.Text>
                        <FontAwesomeIcon icon={faEnvelope} />
                      </InputGroup.Text>
                      <Form.Control
                        name="Email"
                        autoFocus
                        required
                        type="email"
                        placeholder="example@company.com"
                        onChange={(e) => setemail(e.target.value)}
                      />
                    </InputGroup>
                  </Form.Group>

                  <Form.Group>
                    <Form.Group id="password" className="mb-4">
                      <Form.Label>Your Password</Form.Label>
                      <InputGroup>
                        <InputGroup.Text>
                          <FontAwesomeIcon icon={faUnlockAlt} />
                        </InputGroup.Text>
                        <Form.Control
                          name="Password"
                          required
                          type="password"
                          placeholder="Password"
                          onChange={(e) => setpassword(e.target.value)}
                        />
                      </InputGroup>
                    </Form.Group>
                    
                  </Form.Group>
                  <Row className="justify-content-center align-items-center">
                    <Button
                      className="rounded w-50"
                      variant="primary"
                      type="submit"
                    >
                      Sign in
                    </Button>
                  </Row>
                </Form>

                {/* <div className="d-flex justify-content-center align-items-center mt-4">
                  <span className="fw-normal">
                    Not registered?
                    <Card.Link
                      className="fw-bold"
                      as={Link}
                      to={Routes.Signup.path}
                    >
                      {` Create account `}
                    </Card.Link>
                  </span>
                </div> */}

              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default Signin;








